/**
 * Valida solo números
 */
function numeros(e){
   key = e.keyCode || e.which;
   tecla = String.fromCharCode(key).toLowerCase();
   letras = '0123456789';
   especiales = [8];
   tecla_especial = false;
   
   for(var i in especiales){
      if(key == especiales[i]){
         tecla_especial = true;
         break;
      } 
   }
   
   if(letras.indexOf(tecla)==-1 && !tecla_especial) 
      return false;
}

/**
 * Valida solo letras
 */
function letra(e){
   key = e.keyCode || e.which;
   tecla = String.fromCharCode(key).toLowerCase();
   letras = 'áéíóúabcdefghijklmnñopqrstuvwxyz';
   especiales = [8, 32];
   tecla_especial = false;

   for(var i in especiales){
      if(key == especiales[i]){
         tecla_especial = true;
         break;
      } 
   }

   if(letras.indexOf(tecla)==-1 && !tecla_especial)
      return false;
}

